    def get_upload_file_path(self, url):
        "遍历指定目下的所有文件"
        file = os.listdir(str(url))
        filename = list()
        for f in file:
            real_url = os.path.join(url, f)
            if os.path.isfile(real_url):
                logger.info(os.path.abspath(real_url))
                filename.append(os.path.abspath(real_url))
            elif os.path.isdir(real_url):
                self.get_upload_file_path(real_url)
            else:
                logger.info("Error: 文件遍历错误")
                pass
            logger.info(real_url)
        return filename

    def get_signa(self):
        # message加密
        appid = self.appid
        secret_key = self.secret_key
        m2 = hashlib.md5()
        m2.update((appid + self.ts).encode('utf-8'))
        md5 = m2.hexdigest()
        md5 = bytes(md5, encoding='utf-8')
        # 以secret_key为key, 上面的md5为msg， 使用hashlib.sha1加密结果为signa
        signa = hmac.new(secret_key.encode('utf-8'), md5, hashlib.sha1).digest()
        signa = base64.b64encode(signa)
        signa = str(signa, 'utf-8')
        return signa

    def upload_init(self, upload_file_path):
        # 上传数据接口参数构建
        flag = True
        file_len = os.path.getsize(upload_file_path)
        file_name = os.path.basename(upload_file_path)
        logger.info("文件upload路径：{}".format(file_name))
        param_dict = {}
        param_dict['appId'] = self.appid
        param_dict['signa'] = self.signa
        param_dict['ts'] = self.ts
        param_dict["fileSize"] = file_len
        param_dict["fileName"] = file_name
        param_dict["sysDicts"] = "advertisement"
        param_dict["duration"] = "200"
        # 采用轮询的方式访问接口，不再上传回调地址
        # param_dict["callbackUrl"] = self.callbackUrl
        logger.info("upload接口参数：", param_dict)
        data = open(upload_file_path, 'rb').read(file_len)
        if file_len <600:
            logger.info("数据长度较短无法转换")
            flag=False
        return param_dict, data, flag

    def upload(self, upload_file_path):
        # 上传数据
        param_dict, data, flag = self.upload_init(upload_file_path)
        if flag:
            response = requests.post(url=self.lfasr_host + self.api_upload + "?" + urllib.parse.urlencode(param_dict),
                                     headers={"Content-type": "application/json"},
                                     data=data)
            logger.info("upload_url: {}".format(response.request.url))
            result = json.loads(response.text)
            logger.info("upload resp: {}".format(result))
            return result
        else:
            return False

    def download_init(self, uploadresp):
        # 获取数据接口参数构建
        orderId = uploadresp['content']['orderId']
        logger.info("文件查询ID：{}".format(orderId))
        param_dict = {}
        param_dict['appId'] = self.appid
        param_dict['signa'] = self.signa
        param_dict['ts'] = self.ts
        param_dict['orderId'] = orderId
        param_dict['resultType'] = "transfer,predict"
        logger.info("查询部分：")
        logger.info("get result参数：", param_dict)
        return param_dict

    def download(self, uploadresp, item):
        # 下载数据
        param_dict = self.download_init(uploadresp)

        status = 3
        try:
            while status == 3:
                response = requests.post(
                    url=self.lfasr_host + self.api_get_result + "?" + urllib.parse.urlencode(param_dict),
                    headers={"Content-type": "application/json"})
                logger.info("查询中········································································")
                result = json.loads(response.text)
                status = result['content']['orderInfo']['status']
                time.sleep(5)
            logger.info("get_result resp:", result)
            return result

        except Exception as ex:
            logger.error('调用讯飞接口下载文案数据报错：', ex)
            return {}

